import React, { Component } from 'react';
import { useState ,useEffect,useRef} from 'react';
import{faCkeck,faTimes,faInfoCircle} from "@fontawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fontawesome/react-fontawesome";
import { Link } from 'react-router-dom';

const USER_REGEX =/^[a-zA-Z][a-zA-Z0-9-_]{3,23}/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[a-Z])(?=.*[0-9])(?=.*[!@$%]).{8,24}$/;



const register = () => {

    const userRef = useRef() ;
const errRef = useRef() ;

const [pwd, setPwd]=useState('');
const [validPwd, setValidPwd]=useState(false);
const [pwdFocus ,setPwdFocus]=useState(false);

const [matchPwd, setMatchPwd]=useState('');
const [validMatch, setValidMatch]=useState(false);
const [matchFocus ,setMatchFocus]=useState(false);

const[errMsg,setErrMsg] =useState('')
const [success, setSuccess]=useState(fale);

useEffect(()=>{
    userRef.current.focus();
},[])

useEffect(()=>{
const result =USER_REGEX.test(user);
console.log(result);
console.log(user);
setValidName(result);
},[user])

useEffect(()=>{
    const result =PWD_REGEX.test(pwd);
    console.log(result);
    console.log(pwd);
    setValidName(result);
    const match = pwd === matchPwd;
    setValidMatch(match);
    },[pwd,matchPwd])

    useEffect(() =>{
        setErrMsg('');
    },[user,pwd,matchPwd]
    )

    const handleSubmit =async(e) =>{
        e.preventDefault();
    }
    //if button enabled 

    


  return (
    <section>
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live='assertive'>{errMsg}</p>
      <h1>Register</h1>
      <form  onSubmit={handleSubmit}>
        <label htmlFor='username'>
            Username:
            <span className={validName ?"valid" : "hide"}  >
             <FontAwesomeIcon icon ={faCkeck}/>
            </span>
            <span className={validName || !user ? "hide" :"invalid"}  >
             <FontAwesomeIcon icon ={faTimes}/>
            </span>
        </label>
        <input
        type='text'
        id='username'
        ref={userRef}
        autoComplete='off'
        onChange={(e)=>setUser(e.target.value)}
        required
        aria-invalid={setValidName ? "false" :"true"}
        aria-describedby='uidnote'
        onFocus={()=> setUserFocus(true)}
        onBlur={()=>setUserFocus(false)}
        />
        <p id="uidnote" className={userFocus && user && !setValidName ? "instructions" : "offscreen"}>
            <FontAwesomeIcon icon={faInfoCircle}/>
            4 to 24 characters. <br/>
            Must begin with a letter.<br/>
            Letters,numbers,underscores,hyphens allowed
        </p>

        <label htmlFor='password'>
            Password:
            <span className={validPwd ?"valid" : "hide"}  >
             <FontAwesomeIcon icon ={faCkeck}/>
            </span>
            <span className={validPwd|| !pwd ? "hide" :"invalid"}  >
             <FontAwesomeIcon icon ={faTimes}/>
            </span>
        </label>
        <input
        type='password'
        id='password'
        onChange={(e)=>setPwd(e.target.value)}
        required
        aria-invalid={setValidPwd ? "false" :"true"}
        aria-describedby='pwdnote'
        onFocus={()=> setPwdFocus(true)}
        onBlur={()=>setPwdFocus(false)}
        />
        <p id="uidnote" className={pwdFocus  && !validPwd? "instructions" : "offscreen"}>
            <FontAwesomeIcon icon={faInfoCircle}/>
            8 to 24 characters. <br/>
            Must include uppercase and lowercase letters,a number and a special character.<br/>
            Allowd special characters: <span aria-label='exclamation mark'>!</span>
            <span aria-label='at symbol'>@</span>
            <span aria-label='dollar sign'>$</span>
            <span aria-label='percent'>%</span>
        </p>

        <label htmlFor='confirm_pwd'>
            Confirm Password:
            <span className={validMatch ?"valid" : "hide"}  >
             <FontAwesomeIcon icon ={faCkeck}/>
            </span>
            <span className={validMatch || !matchPwdpwd ? "hide" :"invalid"}  >
             <FontAwesomeIcon icon ={faTimes}/>
            </span>
        </label>
        <input
        type='password'
        id='confirm_pwd'
        onChange={(e)=>setMatchPwd(e.target.value)}
        required
        aria-invalid={setMatchPwd ? "false" :"true"}
        aria-describedby='confirmnote'
        onFocus={()=> setMatchFocus(true)}
        onBlur={()=>setMatchFocus(false)}
        />
        <p id="uidnote" className={matchFocus && !validMatch? "instructions" : "offscreen"}>
            <FontAwesomeIcon icon={faInfoCircle}/>
            Must match the first password 
        </p>
        <button disabled={!validName || !validPwd || !validMatch ? true :false}>Register <Now></Now></button>
      </form>
      <p>
        Already registered?<br/>
        <span className='line'>
            <Link to={ Login}></Link>
            </span>
      </p>
    </section>
  );
}

export default register;
