import React from 'react';

const Dashboard = () => {
    const [hostels, setHostels] = useState([]);

  const handleAddHostel = (hostel) => {
    setHostels([...hostels, hostel]);
  };

  const handleDeleteHostel = (id) => {
    setHostels(hostels.filter((hostel) => hostel.id !== id));
  };

  const handleEditHostel = (id, updatedHostel) => {
    setHostels(
      hostels.map((hostel) => {
        if (hostel.id === id) {
          return { ...hostel, ...updatedHostel };
        }
        return hostel;
      })
    );
  };

