import React from "react";
import { useState } from "react";
import SignUp from "./SignUp";
import {Link } from "react-router-dom";

export const Login = (props) => {
    {/*capturing users' data */}
    const{email, setEmail}=useState('');
    const{password, setPassword}=useState('');
    const handleSubmit =()=>{
      email.preventDefault();
      console.log(email);
    }
  
  
    return (
      <div className='form-container'>
      <h2>Login</h2>
      <form  className='login-form'onSubmit={handleSubmit}>
        <label htmlForfor='email'>email</label>
  <input value={email} onChange={(e) => setEmail(e.target.value)} type='email' placeholder='youremail@gmail.com' name='email'></input>
  <label htmlForfor='password'>password</label>
        <input value={password} onChange={(e)=> setPassword(e.target.value)}type='password' placeholder='*******' id='password' name='password'></input>
        <button type='submit'>Login</button>
      </form>
      <button className='link-btn' onClick={() =>props.onFormSwitch(SignUp)}>Don't have an account? Register here.</button>
     <button> <Link to="|Signup">Signup</Link></button>
      </div>

    );
    }
     
      
    
  
  
  export default Login;
